-    Field(x, 5) = Val_int(input->Event.MouseEvent.dwMousePosition.X);
-    bs = input->Event.MouseEvent.dwButtonState;
+    Field(x, 4) = Val_int(input.Event.MouseEvent.dwMousePosition.Y);
